const tabsButtons=document.querySelectorAll(".tabs-btn");tabsButtons.forEach((t=>{t.addEventListener("click",(()=>{tabsButtons.forEach((t=>t.classList.remove("is-active"))),t.classList.add("is-active")}))}));const coursesCards=document.querySelectorAll(".courses-card");tabsButtons.forEach((t=>{t.addEventListener("click",(()=>{const s=t.getAttribute("id");coursesCards.forEach((t=>{"all"===s||t.getAttribute("data-age")===s?t.classList.remove("hidden"):t.classList.add("hidden")})),tabsButtons.forEach((s=>{s===t?s.classList.add("is-active"):s.classList.remove("is-active")}))}))}));